      tags$p("Leonard, Kim. \"Stereotypes in Movies and How Filmmakers Can Avoid Them.\" StudioBinder, 5 Dec. 2019, https://www.studiobinder.com/blog/stereotypes-in-movies/."),
      tags$p("Schacht, Kira. \"What Hollywood Movies Do to Perpetuate Racial Stereotypes.\" DW.COM, 21 Feb. 2019, https://www.dw.com/en/hollywood-movies-stereotypes-prejudice-data-analysis/a-47561660."),
      tags$p("Gonchar, Michael. \"26 Mini-Films for Exploring Race, Bias and Identity With Students.\" The New York Times, 15 Mar. 2017, https://www.nytimes.com/2017/03/15/learning/lesson-plans/25-mini-films-for-exploring-race-bias-and-identity-with-students.html."),
      tags$p("Somvichian-Clausen, Austa. \"Top 10 Films That Break Gender and Racial Stereotypes.\" The Hill, 9 Dec. 2019, https://thehill.com/changing-america/respect/diversity-inclusion/473687-top-10-films-that-break-gender-and-racial."),
      tags$p("\"National Tracking Poll #190524.\" https://morningconsult.com/wp-content/uploads/2019/05/190524_crosstabs_HOLLYWOOD_Adults_v1.pdf. Accessed 4 Feb. 2022. Morning Consult, May 2019."),
    )
  ),
